import React from 'react';
import NewsCardShort from './NewsCardShort'
import NewsDataService from './NewsDataService'
import TagDataService from './TagDataService'
 
class NewsContainer extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            news: [],
            allTags: [],
		}
    }

    componentDidMount() {
        this.refreshNews();
        this.refreshTags();
    }

    refreshNews() {
        NewsDataService.getAllNews()
        .then(
            response => {
                this.setState({news: response.data});
            }
        )
    }

    refreshTags(){
        TagDataService.getAllTags()
        .then(
            response => {
                this.setState({allTags: response.data});
            }
        )
    }

    deleteNews(id) {
        NewsDataService.deleteNews(id)
        .then(
            response => {
                this.refreshNews();
            }
        )
    }

    showOneNews(id) {
        // window.location.href = "/news/" + id;
        this.props.history.push(`/news/` + id);
    }  

    render() {

        const renderAllTagsList = this.state.allTags.map(t => {
            return(
                <option value={t.id}>{t.name}</option>
            )
        })

        const renderNewsCards = this.state.news.map(n => {
            return(
                <NewsCardShort
                newsId={n.id}
                newsTitle={n.title} 
                newsST={n.shortText} 
                newsCrDate={n.creationDate} 
                newsAuthor={n.author}                
                newsTags={n.tags}
                deleteClick={() => this.deleteNews(n.id)}
                readClick={() => this.showOneNews(n.id)}
                toNewsListClick={() => this.showNewsList()}
                deleteClick={() => this.deleteNews(n.id)}
                t = {this.props.t}
                />
            )
        })  
     
		return(
            <div>
                <div className="container">
                    <div className="row">
                        <div className="col-1"></div>
                        <div className="col-10">
                            {/* <div className="input-group mb-3 w-100 my-3">
                            <input id="searchAuthorNameField" type="text" className="form-control" placeholder={this.props.t("new_author_name")} aria-describedby="button-addon2"/>
                            <input id="searchAuthorSurnameField" type="text" className="form-control" placeholder={this.props.t("new_author_surname")} aria-describedby="button-addon2"/>
                                <input type="text" className="form-control" placeholder={this.props.t("authors_name")} aria-describedby="button-addon2"/>
                                <div className="input-group-append">
                                    <button className="btn btn-outline-primary" type="button" id="button-addon2">{this.props.t("search")}</button>
                                </div>
                            </div>
                            <select multiple id="tag-select-field" className="form-control" size="2">
                                {renderAllTagsList}
                            </select> */}

                            <div className="container">

                                <div className="row align-items-center">
                                    <div className="col-10">
                                        <div className="input-group mb-3 w-100 my-3">
                                            <input id="searchAuthorNameField" type="text" className="form-control" placeholder={this.props.t("new_author_name")} aria-describedby="button-addon2"/>
                                            <input id="searchAuthorSurnameField" type="text" className="form-control" placeholder={this.props.t("new_author_surname")} aria-describedby="button-addon2"/>
                                        </div>
                                        <select multiple id="tag-select-field" className="form-control" size="2">
                                            {renderAllTagsList}
                                        </select>
                                    </div>
                                    <div className="col-2 align-self-center">
                                        <button className="btn btn-outline-primary" type="button" id="buttonSearch">{this.props.t("search")}</button>
                                    </div>
                                </div>

                            </div>
                            {renderNewsCards}
                        </div>
                        <div className="col-1"></div>
                    </div>
                </div>
              
            </div>
		);
	}
}

export default NewsContainer
