import React from 'react'

import TagDataService from './TagDataService'

class TagsList extends React.Component{

    constructor(props){
        super(props);
        // this.getAllTagsCallback = this.getAllTagsCallback.bind(this);
        // this.createTagCallback = this.createTagCallback.bind(this);
        // this.saveChangedTagCallback = this.saveChangedTagCallback.bind(this);
        // this.deleteTagCallback = this.deleteTagCallback.bind(this);
        this.state = {
            tags:[],
        }
    }

    componentDidMount() {
        this.refreshTags();
    }

    refreshTags(){
        TagDataService.getAllTags()
        .then(
            response => {
                this.setState({tags: response.data});
            }
        )
    }

    createTag(){
        let newTagName = document.getElementById("newTagField").value;
        let newTag ={id: null, name: newTagName}; 
        TagDataService.createTag(newTag)
        .then(
            response => {
                document.getElementById("newTagField").value = "";
                this.refreshTags();
            }
        )
    }

    saveChangedTag(tagId){
        let tagName = document.getElementById("tagNameField" + tagId).value;
        let tag = {id: tagId, name: tagName};
        TagDataService.editTag(tag)
        .then(
            response => {
                this.refreshTags();
            }
        )
    }

    deleteTag(id){
        TagDataService.deleteTag(id)
        .then(
            response => {
                this.refreshTags();
            }
        )
    }

    // createTagCallback(result){
    //     document.getElementById("newTagField").value = "";
    //     this.refreshTags();
    // }

    render(){
        const renderTagsList = this.state.tags.map(tag => {
            return(
                <div className="row my-1">
                    <div className="col-1"></div>
                    <div className="col-6">
                        <input id={"tagNameField" + tag.id} className="form-control"  type="text" defaultValue={tag.name}/>
                    </div>
                    <div className="col-2">
                        <button type="button" className="btn btn-outline-primary btn-block mx-1" onClick={() => this.saveChangedTag(tag.id)}>{this.props.t("save")}</button>
                    </div>
                    <div className="col-2">
                        <button type="button" className="btn btn-outline-danger btn-block mx-1" onClick={() => this.deleteTag(tag.id)}>{this.props.t("delete")}</button>
                    </div>
                    <div className="col-1"></div>
                </div>
            )
        })  

        return(
            <div className="container">
                <div className="row">
                    <div className="col-1"></div>
                    <div className="col-10">
                    <div className="input-group mb-3 w-100 my-3">
                        <input id="newTagField" type="text" className="form-control" placeholder={this.props.t("new_tag_name")} aria-describedby="button-addon2"/>
                        <div className="input-group-append">
                            <button className="btn btn-outline-success btn-sm" type="button" id="add-tags-button" onClick={() => this.createTag()}>{this.props.t("add")}</button>
                        </div>
                    </div>
 
                    {renderTagsList}

                    </div>
                    <div className="col-1"></div>
                </div>
                
       
                
            </div>
        );
    }
}

export default TagsList
