import './bootstrap.min.css'
// import './bootstrap.min.js'
import $ from 'jquery'
import { Button } from 'reactstrap';

import React from 'react';
import ReactDOM from 'react-dom';

class TagButton extends React.Component {
	render(){
		return(
			<div className="tagButton">
				<Button color="outline-success">{this.props.value}</Button>
			</div>
		);
	}
}

function RenderTags(tags){
	return(
			$.each(tags, function() {
			return(
				alert(this.id + ' : ' + this.name)
				// <div>
				// 	<TagButton value={this.name}/>
				// </div>
			);
		})
	)	
}

function getAllTags() {
	$.ajax({
		url: "http://localhost:8080/news/tags",
		type: "GET",
		dataType: 'json',
		success: (data) => {
			alert(data);
			alert(data.length);
			RenderTags(data)
		}
	});
}

function getAllNews() {
	$.ajax({
		url: "http://localhost:8080/news/news",
		type: "GET",
		dataType: 'json',
		success: (data) => {
			alert(data.length);
			$.each(data, function() {
				return(
					RenderTags(this.tags)
					// <div>
					// 	<TagButton value={this.name}/>
					// </div>
				);
			})
		
		}
	});
}

class Header extends React.Component {
	render(){
		return(
			<nav class="navbar navbar-expand-lg navbar-dark bg-primary">
			
				<a class="navbar-brand" href="#">News Portal</a>
	
				<div class="collapse navbar-collapse" id="navbarSupportedContent">
					<ul class="navbar-nav mr-auto">
						<li class="nav-item active">
							<a class="nav-link" href="#">EN<span class="sr-only">(current)</span></a>
						</li>
						<li class="nav-item active">
							<a class="nav-link" href="#">RU<span class="sr-only">(current)</span></a>
						</li>
					</ul>
				</div>
	
				<div class="collapse navbar-collapse" id="navbarSupportedContent">
					<ul class="navbar-nav mr-auto">
						<li class="nav-item active">
							<a class="nav-link" href="#">Login<span class="sr-only">(current)</span></a>
						</li>
						<li class="nav-item active">
							<a class="nav-link" href="#">Register<span class="sr-only">(current)</span></a>
						</li>
					</ul>
				</div>
				<button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
					<span class="navbar-toggler-icon"></span>
				</button>
	
			</nav>
		);
	}
}

class Footer extends React.Component {
	render(){
		return(
			// <footer class="page-footer font-small blue">
			// 	<div class="footer-copyright text-center py-3">
			// 		© Copyright EPAM 2020: All right reserved
			// 	</div>
			  // </footer>
			  


			<footer class="page-footer font-small cyan darken-3">
			<div class="container">
				<div class="row">
				<div class="col-md-12 py-5">
					<div class="mb-5 flex-center">
					<a class="fb-ic">
						<i class="fab fa-facebook-f fa-lg white-text mr-md-5 mr-3 fa-2x"> </i>
					</a>
					<a class="tw-ic">
						<i class="fab fa-twitter fa-lg white-text mr-md-5 mr-3 fa-2x"> </i>
					</a>
					<a class="gplus-ic">
						<i class="fab fa-google-plus-g fa-lg white-text mr-md-5 mr-3 fa-2x"> </i>
					</a>
					<a class="li-ic">
						<i class="fab fa-linkedin-in fa-lg white-text mr-md-5 mr-3 fa-2x"> </i>
					</a>
					<a class="ins-ic">
						<i class="fab fa-instagram fa-lg white-text mr-md-5 mr-3 fa-2x"> </i>
					</a>
					<a class="pin-ic">
						<i class="fab fa-pinterest fa-lg white-text fa-2x"> </i>
					</a>
					</div>
				</div>
				</div>
			</div>
			<div class="footer-copyright text-center py-3">© 2020 Copyright:
				<a href="https://mdbootstrap.com/"> MDBootstrap.com</a>
			</div>
			</footer>
		);
	}
}

class Site extends React.Component {
	constructor(props){
		super(props);
		this.state = {
			tags: [],
		}
	}

	render(){
		return(
			// getAllNews(),
			<div>
				<Header/> 
				<TagButton value='sdsdsd'/>
				<TagButton value='sdsdsd'/>
				<Footer/>
			</div>
						
		);
	}

}


// ========================================

ReactDOM.render(
  <Site />,
  document.getElementById('root')
);
