import React from 'react'

import TagDataService from './TagDataService'

class TagsList extends React.Component{

    constructor(props){
        super(props);
        this.getAllTagsCallback = this.getAllTagsCallback.bind(this);
        this.state = {
            tags:[],
        }
    }

    componentDidMount() {
        this.refreshTags();
    }

    refreshTags(){
        TagDataService.getAllTags(this.getAllTagsCallback);
    }

    getAllTagsCallback(result){
        this.setState({tags: result});
    }


    render(){
        const func = this.state.tags.map(tag => {
            return(
                <div className="row">
                    <div className="col-10">

                    </div>
                    <div className="col-2">
                    <button type="button" className="btn btn-outline-primary btn-block mx-1" onClick={() => this.editNews()}>Save changes</button>
                    </div>

                </div>
            )
        })  

        return(
            <div className="container">
                <div className="row">
                    <div className="col-1"></div>
                    <div className="col-10">
                        
                    </div>
                    <div className="col-1"></div>
                </div>
                <div className="input-group mb-3 w-100 my-3">
                    <input id="new-tag-field" type="text" className="form-control" placeholder="New tag name" aria-describedby="button-addon2"/>
                    <div className="input-group-append">
                        <button className="btn btn-outline-success btn-sm" type="button" id="add-tags-button" onClick={() => this.setNewTagsToState()}>Add tag</button>
                    </div>
                </div>
 
                {func}
       
                
            </div>
        );
    }
}

export default TagsList
