import React from 'react';
import TagButton from './TagButton'
import TagDataService from './TagDataService'

import $ from 'jquery'

class TagContainer extends React.Component {

    constructor(props){
        super(props);
        this.refreshTags = this.refreshTags.bind(this);
        this.state = {
            tags:[],
		}
    }

    componentDidMount() {
        this.refreshTags();
    }

    refreshTags(){
        TagDataService.getAllTags()
            .then((response) => {
                this.setState({tags: response});
            })
    }

	render(){
        // const func = () => {
        //     $.each(this.props.tags, () => {
        //         return(
        //             <TagButton value={this.name}/>
        //         )
        //     })
        // }
        const func = this.state.tags.map(tag => {
            return(
                <TagButton value={tag.name}/>
            )
        })  
        
		return(
            <div>
                {func}
            </div>
		);
	}
}

export default TagContainer
