import React from 'react';

import $ from 'jquery'

class NewsDataService extends React.Component{

    getAllNews(callback){
        $.ajax({
            url: "http://localhost:8080/news/news",
            type: "GET",
            dataType: 'json',
            success: callback,
        });
    }

    getNewsById(callback, newsId){
        $.ajax({
            url: "http://localhost:8080/news/tags/" + newsId,
            type: "GET",
            dataType: 'json',
            success: callback,
        });
    }

    deleteNews(callback, newsId){
        $.ajax({
            url: "http://localhost:8080/news/tags/" + newsId,
            type: "DELETE",
            dataType: 'json',
            success: callback,
        });
    }
}

export default new NewsDataService()
