import React from 'react';

import $ from 'jquery'

class NewsDataService extends React.Component{

    getAllNews(callback){
        $.ajax({
            url: "http://localhost:8080/news/news",
            type: "GET",
            dataType: 'json',
            success: callback,
        });
    }

    getNewsById(callback, newsId){
        $.ajax({
            url: "http://localhost:8080/news/news/" + newsId,
            type: "GET",
            dataType: 'json',
            success: callback,
        });
    }

    deleteNews(callback, newsId){
        $.ajax({
            url: "http://localhost:8080/news/news/" + newsId,
            type: "DELETE",
            dataType: 'json',
            success: callback,
        });
    }

    createNews(callback, news){
        $.ajax({
            url: "http://localhost:8080/news/news",
            type: "POST",
            dataType: 'json',
            contentType: 'application/json',
            data: JSON.stringify(news),
            success: callback,
        });
    }

    editNews(callback, news){
        $.ajax({
            url: "http://localhost:8080/news/news",
            type: "PUT",
            dataType: 'json',
            contentType: 'application/json',
            data: JSON.stringify(news),
            success: callback,
        });
    }
}

export default new NewsDataService()
