import React from 'react'

import TagDataService from './TagDataService'

class TagsList extends React.Component{

    constructor(props){
        super(props);
        this.getAllTagsCallback = this.getAllTagsCallback.bind(this);
        this.createTagCallback = this.createTagCallback.bind(this);
        this.saveChangedTagCallback = this.saveChangedTagCallback.bind(this);
        this.deleteTagCallback = this.deleteTagCallback.bind(this);
        this.state = {
            tags:[],
        }
    }

    componentDidMount() {
        this.refreshTags();
    }

    refreshTags(){
        TagDataService.getAllTags(this.getAllTagsCallback);
    }

    createTag(){
        let newTagName = document.getElementById("newTagField").value;
        let newTag ={id: null, name: newTagName}; 
        console.log(newTag);
        TagDataService.createTag(this.createTagCallback, newTag);
    }

    saveChangedTag(tagId){
        let tagName = document.getElementById("tagNameField" + tagId).value;
        let tag = {id: tagId, name: tagName};
        TagDataService.editTag(this.saveChangedTagCallback, tag);
    }

    deleteTag(id){
        TagDataService.deleteTag(this.deleteTagCallback, id);
    }

    getAllTagsCallback(result){
        this.setState({tags: result});
    }

    createTagCallback(result){
        document.getElementById("newTagField").value = "";
        this.refreshTags();
    }

    saveChangedTagCallback(result){
        window.location.href = "/tags/edit/";
    }

    deleteTagCallback(){
        this.refreshTags();
    }

    render(){
        const renderTagsList = this.state.tags.map(tag => {
            return(
                <div className="row my-1">
                    <div className="col-1"></div>
                    <div className="col-6">
                        <input id={"tagNameField" + tag.id} className="form-control"  type="text" defaultValue={tag.name}/>
                    </div>
                    <div className="col-2">
                        <button type="button" className="btn btn-outline-primary btn-block mx-1" onClick={() => this.saveChangedTag(tag.id)}>Save changes</button>
                    </div>
                    <div className="col-2">
                        <button type="button" className="btn btn-outline-danger btn-block mx-1" onClick={() => this.deleteTag(tag.id)}>Delete tag</button>
                    </div>
                    <div className="col-1"></div>
                </div>
            )
        })  

        return(
            <div className="container">
                <div className="row">
                    <div className="col-1"></div>
                    <div className="col-10">
                    <div className="input-group mb-3 w-100 my-3">
                        <input id="newTagField" type="text" className="form-control" placeholder="New tag name" aria-describedby="button-addon2"/>
                        <div className="input-group-append">
                            <button className="btn btn-outline-success btn-sm" type="button" id="add-tags-button" onClick={() => this.createTag()}>Add tag</button>
                        </div>
                    </div>
 
                    {renderTagsList}

                    </div>
                    <div className="col-1"></div>
                </div>
                
       
                
            </div>
        );
    }
}

export default TagsList
