import React from 'react'

import AuthorDataService from './AuthorDataService'

class AuthorsList extends React.Component{

    constructor(props){
        super(props);
        // this.getAllAuthorsCallback = this.getAllAuthorsCallback.bind(this);
        // this.createAuthorCallback = this.createAuthorCallback.bind(this);
        // this.deleteAuthorCallback = this.deleteAuthorCallback.bind(this);
        // this.saveChangedAuthorCallback = this.saveChangedAuthorCallback.bind(this);
        this.state = {
            authors:[],
        }
    }

    componentDidMount() {
        this.refreshAuthors();
    }

    refreshAuthors(){
        AuthorDataService.getAllAuthors()
        .then(
            response => {
                this.setState({authors: response.data});
            }
        )
    }

    createAuthor(){
        let newAuthorName = document.getElementById("newAuthorNameField").value;
        let newAuthorSurname = document.getElementById("newAuthorSurnameField").value;
        let newAuthor ={id: null, name: newAuthorName, surname: newAuthorSurname}; 
        AuthorDataService.createAuthor(newAuthor)
        .then(
            response => {
                this.refreshAuthors();
            }
        )
    }

    saveChangedAuthor(authorId){
        let authorName = document.getElementById("authorNameField" + authorId).value;
        let authorSurname = document.getElementById("authorSurnameField" + authorId).value;
        let author ={id: authorId, name: authorName, surname: authorSurname}; 
        AuthorDataService.editAuthor(author)
        .then(
            response => {
                this.refreshAuthors();
            }
        )
    }

    deleteAuthor(id){
        AuthorDataService.deleteAuthor(id)
        .then(
            response => {
                this.refreshAuthors();
            }
        )
    }

    // getAllAuthorsCallback(result){
    //     this.setState({authors: result});
    // }

    // createAuthorCallback(result){
    //     this.refreshAuthors();
    // }

    // saveChangedAuthorCallback(result){
    //     this.refreshAuthors();
    // }

    // deleteAuthorCallback(result){
    //     this.refreshAuthors();
    // }

    render(){

        const renderAuthorsList = this.state.authors.map(author => {
            return(
                <div className="row my-1">
                    <div className="col-1"></div>
                    <div className="col-3">
                        <input id={"authorNameField" + author.id} className="form-control"  type="text" defaultValue={author.name}/>
                    </div>
                    <div className="col-3">
                        <input id={"authorSurnameField" + author.id} className="form-control"  type="text" defaultValue={author.surname}/>
                    </div>
                    <div className="col-2">
                        <button type="button" className="btn btn-outline-primary btn-block mx-1" onClick={() => this.saveChangedAuthor(author.id)}>{this.props.t("save")}</button>
                    </div>
                    <div className="col-2">
                        <button type="button" className="btn btn-outline-danger btn-block mx-1" onClick={() => this.deleteAuthor(author.id)}>{this.props.t("delete")}</button>
                    </div>
                    <div className="col-1"></div>
                </div>
            )
        }); 

        return(

            <div className="container">
            <div className="row">
                <div className="col-1"></div>
                <div className="col-10">
                <div className="input-group mb-3 w-100 my-3">
                    <input id="newAuthorNameField" type="text" className="form-control" placeholder={this.props.t("new_author_name")} aria-describedby="button-addon2"/>
                    <input id="newAuthorSurnameField" type="text" className="form-control" placeholder={this.props.t("new_author_surname")} aria-describedby="button-addon2"/>
                    <div className="input-group-append">
                        <button className="btn btn-outline-success btn-sm" type="button" id="add-tags-button" onClick={() => this.createAuthor()}>{this.props.t("add")}</button>
                    </div>
                </div>

                {renderAuthorsList}

                </div>
                <div className="col-1"></div>
            </div>
            
   
            
        </div>

        );
    }
}

export default AuthorsList
