import React from 'react'
import NewsDataService from './NewsDataService'

class NewsEditForm extends React.Component {

    constructor(props){
        super(props);
        this.getNewsByIdCallback = this.getNewsByIdCallback.bind(this);
        this.state = {
            newsId: {},
            newsTitle: {},
            newsShortText: {},
            newsFullText: {},
            author: {},
            tags: [],
		}
    }

    componentDidMount() {
        this.refreshNews(this.props.match.params.id);
    }

    refreshNews(id) {
        NewsDataService.getNewsById(this.getNewsByIdCallback, id);
    }

    getNewsByIdCallback(result) {
        this.setState({newsId: result.id});
        this.setState({newsTitle: result.title});
        this.setState({newsShortText: result.shortText});
        this.setState({newsFullText: result.fullText});
        this.setState({author: result.author});
        this.setState({tags: result.tags});
    }  

    render(){
        return(
            <div>
                <form>
                    <div class="form-group">
                        <label for="exampleInputEmail1">Title</label>
                        <input type="text" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp"
                            value={this.state.newsTitle}/>
                    </div>
                    <div class="form-group">
                        <label for="exampleFormControlTextarea1">Example textarea</label>
                        <textarea class="form-control" id="exampleFormControlTextarea1" rows="3">{this.state.newsTitle}</textarea>
                    </div>
                    <button type="submit" class="btn btn-primary">Submit</button>
                </form>
            </div>
        );
    }
}

export default NewsEditForm
