import React from 'react';

import $ from 'jquery'

class TagDataService extends React.Component{

    getAllTags(callback){
        $.ajax({
            url: "http://localhost:8080/news/tags",
            type: "GET",
            dataType: 'json',
            success: callback,
        });
    }

    getTagById(callback, tagId){
        $.ajax({
            url: "http://localhost:8080/news/tags/" + tagId,
            type: "GET",
            dataType: 'json',
            success: callback,
        });
    }

    deleteTag(callback, tagId){
        $.ajax({
            url: "http://localhost:8080/news/tags/" + tagId,
            type: "DELETE",
            dataType: 'json',
            success: callback,
        });
    }

    createTag(callback, tag){
        $.ajax({
            url: "http://localhost:8080/news/tags",
            type: "POST",
            dataType: 'json',
            contentType: 'application/json',
            data: JSON.stringify(tag),
            success: callback,
        });
    }

    editTag(callback, tag){
        $.ajax({
            url: "http://localhost:8080/news/tags",
            type: "PUT",
            dataType: 'json',
            contentType: 'application/json',
            data: JSON.stringify(tag),
            success: callback,
        });
    }
}

export default new TagDataService()
