import React from 'react';

import axios from 'axios'
import $ from "jquery"

const TAGS_URL = 'tags'
const ROOT_API_URL = 'http://localhost:8080'
const TAGS_API_URL = `${ROOT_API_URL}/news/${TAGS_URL}`

class TagDataService extends React.Component{

    getAllTags(callback){
        return axios.get(`${TAGS_API_URL}`);
    }

    getTagById(callback, tagId){
        $.ajax({
            url: "http://localhost:8080/news/tags/" + tagId,
            type: "GET",
            dataType: 'json',
            success: callback,
        });
    }

    deleteTag(callback, tagId){
        return axios.delete(`${TAGS_API_URL}/${tagId}`);
        $.ajax({
            url: "http://localhost:8080/news/tags/" + tagId,
            type: "DELETE",
            dataType: 'json',
            success: callback,
        });
    }

    createTag(callback, tag){
        $.ajax({
            url: "http://localhost:8080/news/tags",
            type: "POST",
            dataType: 'json',
            contentType: 'application/json',
            data: JSON.stringify(tag),
            success: callback,
        });
    }

    editTag(callback, tag){
        $.ajax({
            url: "http://localhost:8080/news/tags",
            type: "PUT",
            dataType: 'json',
            contentType: 'application/json',
            data: JSON.stringify(tag),
            success: callback,
        });
    }
}

export default new TagDataService()
