import React from 'react'

class NewsEditForm extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            newsId: {},
            newsTitle: {},
            newsShortText: {},
            newsFullText: {},
            author: {},
            tags: [],
		}
    }

    componentDidMount() {
        this.refreshNews(this.props.match.params.id);
    }

    refreshNews(id) {
        NewsDataService.getNewsById(this.getNewsByIdCallback, id);
    }

    getNewsByIdCallback(result) {
        this.setState({newsId: result.id});
        this.setState({newsTitle: result.title});
        this.setState({newsShortText: result.shortText});
        this.setState({newsFullText: result.fullText});
        this.setState({author: result.author});
        this.setState({tags: result.tags});
    }  

    render(){
        return(
            <div>
                <form>
                    <div class="form-group">
                        <label for="exampleInputEmail1">Email address</label>
                        <input type="email" class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp"/>
                    </div>
                    <div class="form-group">
                        <label for="exampleInputPassword1">Password</label>
                        <input type="password" class="form-control" id="exampleInputPassword1"/>
                    </div>
                    <button type="submit" class="btn btn-primary">Submit</button>
                </form>
            </div>
        );
    }
}

export default NewsEditForm
