import React from 'react'

import TagDataService from './TagDataService'

class TagsList extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            tags:[],
        }
    }

    componentDidMount() {
        this.refreshTags();
    }

    refreshTags(){
        TagDataService.getAllTags()
        .then(
            response => {
                this.setState({tags: response.data});
            }
        )
    }

    createTag(){
        
        let previousErrorMessage = document.getElementById("tagNameErrorMessage");
        if (previousErrorMessage) {
            previousErrorMessage.remove();
        }
        let newTagInput = document.getElementById("newTagField");
        let errorMessage = this.validateNewsTagValue(newTagInput.value);
        if (errorMessage) {
            let small = document.createElement('small');
            div.id = "tagNameErrorMessage";
            div.className = "text-danger";
            div.innerText = errorMessage;
            console.log(div);
            let fieldSet = document.getElementById("newTagInputGroup");
            fieldSet.after(div);
            return;
        }


        let newTagName = document.getElementById("newTagField").value;
        let newTag ={id: null, name: newTagName}; 
        TagDataService.createTag(newTag)
        .then(
            response => {
                document.getElementById("newTagField").value = "";
                this.refreshTags();
            }
        )
    }

    saveChangedTag(tagId){
        let tagName = document.getElementById("tagNameField" + tagId).value;
        let tag = {id: tagId, name: tagName};
        TagDataService.editTag(tag)
        .then(
            response => {
                this.refreshTags();
            }
        )
    }

    deleteTag(id){
        TagDataService.deleteTag(id)
        .then(
            response => {
                this.refreshTags();
            }
        )
    }

    // validateNewTag() {
    //     let previousErrorMessage = document.getElementById("tagNameErrorMessage");
    //     if (previousErrorMessage) {
    //         previousErrorMessage.remove();
    //     }
    //     let newTagInput = document.getElementById("newTagField");
    //     let errorMessage = this.validateNewsTagValue(newTagInput.value);
    //     if (errorMessage) {
    //         let div = document.createElement('div');
    //         div.id = "tagNameErrorMessage";
    //         div.className = "alert alert-warning";
    //         div.innerText = errorMessage;
    //         console.log(div);
    //         let fieldSet = document.getElementById("newTagField");
    //         fieldSet.after(div);
    //         return;
    //     }
    // }

    validateNewsTagValue(value) {
        let errorMessage = "";
        if (!value) {
            errorMessage = "Tag name must be not empty";
        } else if (Number.parseInt(value)) {
            errorMessage = "Tag name must consists of characters";
        } else if (value.length > 30) {
            errorMessage = "Tag name must be no larger than 30 characters";
        } else {
            const allTags = this.state.allTags;
            for (let tag of allTags) {
                if (tag.name === value) {
                    errorMessage = "Tag with such name already exists";
                    break;
                }
            }
        }
        return errorMessage;
    }

    render(){
        const renderTagsList = this.state.tags.map(tag => {
            return(
                <div className="row my-1">
                    <div className="col-1"></div>
                    <div className="col-6">
                        <input id={"tagNameField" + tag.id} className="form-control"  type="text" defaultValue={tag.name}/>
                    </div>
                    <div className="col-2">
                        <button type="button" className="btn btn-outline-primary btn-block mx-1" onClick={() => this.saveChangedTag(tag.id)}>{this.props.t("save")}</button>
                    </div>
                    <div className="col-2">
                        <button type="button" className="btn btn-outline-danger btn-block mx-1" onClick={() => this.deleteTag(tag.id)}>{this.props.t("delete")}</button>
                    </div>
                    <div className="col-1"></div>
                </div>
            )
        })  

        return(
            <div className="container">
                <div className="row">
                    <div className="col-1"></div>
                    <div className="col-10">
                    <div id="newTagInputGroup" className="input-group mb-3 w-100 my-3">
                        <input id="newTagField" type="text" className="form-control" placeholder={this.props.t("new_tag_name")} aria-describedby="button-addon2"/>
                        <div className="input-group-append">
                            <button className="btn btn-outline-success btn-sm" type="button" id="add-tags-button" onClick={() => this.createTag()}>{this.props.t("add")}</button>
                        </div>
                    </div>
 
                    {renderTagsList}

                    </div>
                    <div className="col-1"></div>
                </div>
                
       
                
            </div>
        );
    }
}

export default TagsList
