import 'bootstrap/dist/css/bootstrap.min.css'
import 'jquery/dist/jquery.min.js'
import 'bootstrap/dist/js/bootstrap.min.js'

import './index.css'

import $ from 'jquery'

import Header from './component/Header'
import Footer from './component/Footer'
import TagButton from './component/TagButton'
import NewsPage from './component/NewsPage'
import NewsCardFull from './component/NewsCardFull'

import {
BrowserRouter as Router,
Switch,
Route
  } from"react-router-dom";




import React from 'react';
import ReactDOM from 'react-dom';

class Site extends React.Component {
	constructor(props){
		super(props);
		this.state = {
			tags: [],
			news: [],
		}
	}

	componentDidMount() {
		document.body.style.paddingTop = '60px';
		document.body.style.paddingBottom = '65px';
		document.body.style.marginLeft = 0;
		document.body.style.marginRight = 0;
	}

	getAllNews() {
		$.ajax({
			url: "http://localhost:8080/news/news",
			type: "GET",
			dataType: 'json',
			success: (data) => {
				this.setState({news: data});			
			}
		});
	}

	RenderTags(tags){
		return(
				$.each(tags, function() {
				return(
					<TagButton value={this.name}/>
				);
			})
		)	
	}

	render(){
		return(
			<div>
				<Header/> 
				<div className="container-fluid" role="main">
					<div className="row">
						<div className="col-2" id="left"> 
						    <div className="mx-1 my-3">
								<div className="list-group w-100">
									<a href="#" className="list-group-item">Link</a>
									<a href="#" className="list-group-item">Link</a>
									<a href="#" className="list-group-item">Link</a>
								</div>
							</div> 
					    </div>
						<div className="col-10 ">
						{/* <NewsPage/> */}

						<Router>
					
							<Switch>
								<Route path="/" exact component={NewsPage} />
								<Route path="/news" exact component={NewsPage} />
								<Route path="/news/:id" component={NewsCardFull} />
							</Switch>
					
           				</Router>

							{/* <div className="my-3 border-0">
								<div className="card w-75 border-0">
									<TagContainer />
								</div>
							</div> */}

						</div>
					</div>
				
					<Footer/> 
					
				</div>	

			

			</div>
						
		);
	}
}

// ========================================

ReactDOM.render(
  <Site/>,
  document.getElementById('root')
);
