import React from 'react';
import NewsCardShort from './NewsCardShort'
import NewsDataService from './NewsDataService'
 
class NewsContainer extends React.Component {
    constructor(props){
        super(props);
        this.getAllNewsCallback = this.getAllNewsCallback.bind(this);
        this.deleteNewsCallback = this.deleteNewsCallback.bind(this);
        this.state = {
            news:[],
            message: "",
		}
    }

    componentDidMount() {
        this.refreshNews();
    }

    refreshNews() {
        NewsDataService.getAllNews(this.getAllNewsCallback);
    }

    deleteNews(id) {
        NewsDataService.deleteNews(this.deleteNewsCallback, id);
    }

    getAllNewsCallback(result) {
        this.setState({news: result});
    }   

    deleteNewsCallback() {
        this.setState({message: 'News was deleted'});
        this.refreshNews();
    }

    showOneNews(id) {
        this.props.history.push(`/news/` + id);
    }  

    render() {
        const func = this.state.news.map(n => {
            console.log(n.id + n.author);
            return(
                <NewsCardShort
                newsId={n.id}
                newsTitle={n.title} 
                newsST={n.shortText} 
                newsCrDate={n.creationDate} 
                newsAuthor={n.author}                
                newsTags={n.tags}
                deleteClick={() => this.deleteNews(n.id)}
                readClick={() => this.showOneNews(n.id)}
                toNewsListClick={() => this.showNewsList()}
                deleteClick={() => this.deleteNews(n.id)}
                />
            )
        })  
        
		return(
            <div>
                <div className="container">
                    <div className="row">
                        <div className="col-1"></div>
                        <div className="col-10">
                            <div className="input-group mb-3 w-100 my-3">
                                <input type="text" className="form-control" placeholder="Author's name" aria-describedby="button-addon2"/>
                                <div className="input-group-append">
                                    <button className="btn btn-outline-primary" type="button" id="button-addon2">Search</button>
                                </div>
                            </div>
                            {func}
                        </div>
                        <div className="col-1"></div>
                    </div>
                </div>
              
            </div>
		);
	}
}

export default NewsContainer
