import React from 'react';
import NewsCard from './NewsCard'
import NewsDataService from './NewsDataService'

class NewsContainer extends React.Component {
    constructor(props){
        super(props);
        this.getAllNewsCallback = this.getAllNewsCallback.bind(this);
        this.deleteNewsCallback = this.deleteNewsCallback.bind(this);
        this.state = {
            news:[],
            message: "",
		}
    }

    componentDidMount() {
        this.refreshTags();
    }

    refreshNews(){
        NewsDataService.getAllNews(this.getAllNewsCallback);
    }

    deleteNews(id){
        NewsDataService.deleteNews(this.deleteNewsCallback, id);
    }

    getAllNewsCallback(result){
        this.setState({news: result});
    }

    deleteNewsCallback(){
        this.setState({message: 'News was deleted'});
        this.refreshNews();
    }

    render(){
        const func = this.state.news.map(n => {
            return(
                <NewsCard
                newsId={n.id}
                newsTitle={n.title} 
                newsST={n.shortText} 
                newsFT={n.fullText} 
                newsCrDate={n.creationDate} 
                newsAuthor={n.author}
                deleteClick={() => this.deleteNews(n.id)}/>
            )
        })  
        
		return(
            <div>
                {func}
            </div>
		);
	}
}

export default NewsContainer
