import React from 'react'

class AuthorsList extends React.Component{

    constructor(props){
        super(props);
        this.getAllAuthorsCallback = this.getAllAuthorsCallback.bind(this);
        this.createAuthorCallback = this.createAuthorCallback.bind(this);
        this.saveChangedAuthorCallback = this.saveChangedAuthorCallback.bind(this);
        this.deleteAuthorCallback = this.deleteAuthorCallback.bind(this);
        this.state = {
            authors:[],
        }
    }

    render(){

        const renderAuthorsList = this.state.authors.map(author => {
            return(
                <div className="row my-1">
                    <div className="col-1"></div>
                    <div className="col-3">
                        <input id={"authorNameField" + author.id} className="form-control"  type="text" defaultValue={author.name}/>
                    </div>
                    <div className="col-3">
                        <input id={"authorSurnameField" + author.id} className="form-control"  type="text" defaultValue={author.surname}/>
                    </div>
                    <div className="col-2">
                        <button type="button" className="btn btn-outline-primary btn-block mx-1" onClick={() => this.saveChangedAuthor(author.id)}>Save changes</button>
                    </div>
                    <div className="col-2">
                        <button type="button" className="btn btn-outline-danger btn-block mx-1" onClick={() => this.deleteAurhor(author.id)}>Delete author</button>
                    </div>
                    <div className="col-1"></div>
                </div>
            )
        }); 

        return(
        <div>{renderAuthorsList}</div>
        );
    }
}

export default AuthorsList
