import React from 'react';
import NewsCardShort from './NewsCardShort'
import NewsDataService from './NewsDataService'
import NewsCardFull from './NewsCardFull'

import {
    BrowserRouter as Router,
    Switch,
    Route
      } from"react-router-dom";

class NewsContainer extends React.Component {
    constructor(props){
        super(props);
        this.getAllNewsCallback = this.getAllNewsCallback.bind(this);
        this.deleteNewsCallback = this.deleteNewsCallback.bind(this);
        this.showOneNews = this.showOneNews.bind(this);
        this.state = {
            news:[],
            message: "",
		}
    }

    componentDidMount() {
        this.refreshNews();
    }

    refreshNews() {
        NewsDataService.getAllNews(this.getAllNewsCallback);
    }

    deleteNews(id) {
        NewsDataService.deleteNews(this.deleteNewsCallback, id);
    }

    getAllNewsCallback(result) {
        this.setState({news: result});
    }   

    deleteNewsCallback() {
        this.setState({message: 'News was deleted'});
        this.refreshNews();
    }

    showOneNews(id) {
        this.props.history.push(`/news/` + id);
    }  

    render() {
        const func = this.state.news.map(n => {
           
            return(
                <NewsCardShort
                newsId={n.id}
                newsTitle={n.title} 
                newsST={n.shortText} 
                newsCrDate={n.creationDate} 
                newsAuthor={n.author}                
                newsTags={n.tags}
                deleteClick={() => this.deleteNews(n.id)}
                readClick={() => this.showOneNews(n.id)}
                />
            )
        })  
        
		return(
            <div>
                {func}
                <Router>
					<>
						<Switch>
							<Route path="/news/:id" component={NewsCardFull} />
						</Switch>
					</>
           		</Router>
            </div>
		);
	}
}

export default NewsContainer
