import 'bootstrap/dist/css/bootstrap.min.css'
import 'jquery/dist/jquery.min.js'
import 'bootstrap/dist/js/bootstrap.min.js'

import './index.css'

import $ from 'jquery'

import Header from './component/Header'
import Footer from './component/Footer'
import TagButton from './component/TagButton'
import NewsCard from './component/NewsCard'
import TagContainer from './component/TagContainer'
import NewsContainer from './component/NewsContainer'

import React from 'react';
import ReactDOM from 'react-dom';

class Site extends React.Component {
	constructor(props){
		super(props);
		this.state = {
			tags: [],
			news: [],
		}
	}

	componentDidMount() {
		document.body.style.paddingTop = '60px';
		document.body.style.paddingBottom = '65px';
		document.body.style.marginLeft = 300;
		document.body.style.marginRight = 0;
	}

	getAllNews() {
		$.ajax({
			url: "http://localhost:8080/news/news",
			type: "GET",
			dataType: 'json',
			success: (data) => {
				this.setState({news: data});			
			}
		});
	}

	RenderTags(tags){
		return(
				$.each(tags, function() {
				return(
					<TagButton value={this.name}/>
				);
			})
		)	
	}

	render(){
		return(
			<div>
				<Header/> 
				<div className="container-fluid" role="main">
					<div className="row">
						<div className="col-2" id="left"> 
						    <div className="mx-1 my-3">
								<div className="list-group w-100">
									<a href="#" className="list-group-item">Link</a>
									<a href="#" className="list-group-item">Link</a>
									<a href="#" className="list-group-item">Link</a>
								</div>
							</div> 
					    </div>
						<div className="col-10 ">
							<div className="container">
								<div className="row">
									<div className="col-1"></div>
									<div className="col-10">
										<div className="input-group mb-3 w-100 my-3">
											<input type="text" className="form-control" placeholder="Author's name" aria-label="Author's name" aria-describedby="button-addon2"/>
											<div className="input-group-append">
												<button className="btn btn-outline-primary" type="button" id="button-addon2">Search</button>
											</div>
										</div>
						
										<NewsContainer/>
									</div>
									<div className="col-1"></div>
								</div>
							</div>

							{/* <div className="my-3 border-0">
								<div className="card w-75 border-0">
									<TagContainer />
								</div>
							</div> */}

						
						</div>
					</div>
				
					<Footer/> 
					
				</div>	
			</div>
						
		);
	}
}

// ========================================

ReactDOM.render(
  <Site/>,
  document.getElementById('root')
);
