import React from 'react';
import TagContainerSimple from './TagContainerSimple'
import NewsDataService from './NewsDataService'

class NewsCardFull extends React.Component {
    constructor(props){
        super(props);
        this.getAllNewsCallback = this.getAllNewsCallback.bind(this);
        this.state = {
            news:{},
            author: {},
            tags:[],
		}
    }

    componentDidMount() {
        this.refreshNews(this.props.match.params.id);
    }

    refreshNews(id) {
        NewsDataService.getNewsById(this.getAllNewsCallback, id);
    }

    getAllNewsCallback(result) {
        this.setState({news: result});
        this.setState({author: result.author});
        this.setState({tags: result.tags});
    }  

	render(){
        console.log(this.state.news)
        let author = this.state.news.author;
		return(
            <div className="my-3">
                <div className="card w-100">
                    <h4 className="card-header alert alert-primary">{this.state.news.title}</h4>
                    <div className="card-body">
                        <div className="container">
                            <div className="row">
                                {console.log(author)}
                                <div className="col-6">
                                    <p className="card-title">{author.name + ' ' + author.surname}</p>
                                </div>
                                <div className="col-4"></div>
                                <div className="col-2">
                                    <p className="card-text" style={{fontSize: 14 + 'px'}}>{this.state.news.creationDate}</p>
                                </div>
                            </div>
                            <div className="row">
                                <div className="col">
                                    <h6 className="card-title">{this.state.news.shortText}</h6>
                                    <p className="card-text">{this.state.news.fullText}</p>
                                </div>
                            </div>
                            <div className="row">
                                {/* <div className="col my-3">
                                    <TagContainerSimple tags={this.state.news.tags}/>
                                </div> */}
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        
		);
	}
}

export default NewsCardFull
