import React from 'react'
import NewsDataService from './NewsDataService'
import TagDataService from './TagDataService'
import TagButton from './TagButton'

import $ from 'jquery'

class NewsEditForm extends React.Component {

    constructor(props){
        super(props);
        this.getNewsByIdCallback = this.getNewsByIdCallback.bind(this);
        this.getAllTagsCallback = this.getAllTagsCallback.bind(this);
        this.state = {
            newsId: {},
            newsTitle: '',
            newsShortText: '',
            newsFullText: '',
            author: {},
            tags: [],
            allTags: [],
		}
    }

    componentDidMount() {
        this.refreshNews(this.props.match.params.id);
        this.refreshTagsList();
    }

    refreshNews(id) {
        NewsDataService.getNewsById(this.getNewsByIdCallback, id);
    }

    refreshTagsList(){
        TagDataService.getAllTags(this.getAllTagsCallback);
    }

    getAllTagsCallback(result){
        this.setState({allTags: result});
    }

    getNewsByIdCallback(result) {
        this.setState({newsId: result.id});
        this.setState({newsTitle: result.title});
        this.setState({newsShortText: result.shortText});
        this.setState({newsFullText: result.fullText});
        this.setState({author: result.author});
        this.setState({tags: result.tags});
    }  

    setNewTagsToState(){
        let stateTagsArray = [];
        stateTagsArray = this.state.tags;
        let newTagFieldValue = document.getElementById("new-tag-field").value;
        let selectTagFieldValue = document.getElementById("tag-select-field").childNodes; 


        for(var i=0; i<selectTagFieldValue.length; i++){
            if(selectTagFieldValue[i].selected){
                // for(var j=0; i<stateTagsArray.length; j++){
                    // console.log(stateTagsArray[j]);
                    // if(stateTagsArray[j] !== selectTagFieldValue[i].textContent){
                        stateTagsArray.push({id: null, name: selectTagFieldValue[i].textContent});
                    // }
                // }
                
            }
        }

        let thisTagContainsInState = false;

        for(var k=0; i<stateTagsArray.length; k++){
            console.log(stateTagsArray[k].name);
            console.log(newTagFieldValue);
            if(stateTagsArray[k].name == newTagFieldValue){
                thisTagContainsInState = true;
                break;
            }
        }

        if((newTagFieldValue.length !== 0) && (!thisTagContainsInState)){
            stateTagsArray.push({id: null, name: newTagFieldValue});
        }

        this.setState({tags: stateTagsArray});
    }

    render(){

        const renderAllTagsList = this.state.allTags.map(t => {
            return(
            <option>{t.name}</option>
            )
        })

        const renderThisNewsTags = this.state.tags.map(tag => {
            return(
                <TagButton 
                tagName={tag.name}
                // deleteClick={() => this.deleteTag(tag.id)}
                />
            )
        }) 

        return(

            <div className="container">
            <div className="row">
                <div className="col-1"></div>
                <div className="col-10">
                    <div>
                        <form>
                            <div className="form-group">
                                <label for="newsTitleField">Title</label>
                                <input type="text" className="form-control" id="newsTitleField" maxlength="30" value={this.state.newsTitle}/>
                            </div>
                            <div className="form-group">
                                <label for="newsShortTextField">Short text</label>
                                <textarea className="form-control" id="newsShortTextField"  maxlength="100" rows="2" value={this.state.newsShortText}></textarea>
                            </div>
                            <div className="form-group">
                                <label for="newsFullTextField">Full text</label>
                                <textarea className="form-control" id="newsFullTextField"  maxlength="2000" rows="8" value={this.state.newsFullText}></textarea>
                            </div>
                            <div className="form-group">
                                <div className="container">
                                    <div className="row">
                                        <div className="col-6">
                                            <label>Selected tags</label>
                                            <div>
                                                {renderThisNewsTags}
                                            </div>
                                        </div>
                                        <div className="col-6">
                                            <label for="tagsMultiselectField">Select tags</label>
                                            <select multiple  id="tag-select-field" className="form-control">
                                                {renderAllTagsList}
                                            </select>
                                            <div>
                                                <div className="input-group mb-3 w-100 my-3">
                                                    <input id="new-tag-field" type="text" className="form-control" placeholder="New tag name" aria-describedby="button-addon2"/>
                                                    <div className="input-group-append">
                                                        <button className="btn btn-outline-success btn-sm" type="button" id="add-tags-button" onClick={() => this.setNewTagsToState()}>Add tags</button>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                             <button type="button" className="btn btn-primary">Save changes</button>
                        </form>
                    </div>
                </div>
                <div className="col-1"></div>
            </div>
        </div>

           
        );
    }
}

export default NewsEditForm
