		}
	}

	getAllTags() {
		$.ajax({
			url: "http://localhost:8080/news/tags",
			type: "GET",
			dataType: 'json',
			success: (data) => {	
				alert(data.length);
				RenderTags(data)
			}
		});
	}

	getAllNews() {
		$.ajax({
			url: "http://localhost:8080/news/news",
			type: "GET",
			dataType: 'json',
			success: (data) => {
