import './bootstrap.min.css'
// import './bootstrap.min.js'
import $ from 'jquery'

import Header from './component/Header'
import Footer from './component/Footer'
import TagButton from './component/TagButton'
import NewsCard from './component/NewsCard'
import TagContainer from './component/TagContainer'

import React from 'react';
import ReactDOM from 'react-dom';

class Site extends React.Component {
	constructor(props){
		super(props);
		this.state = {
			tags: [],
			news: [],
		}
	}

	getAllTags() {
		$.ajax({
			url: "http://localhost:8080/news/tags",
			type: "GET",
			dataType: 'json',
			success: (data) => {	
				this.setState({tags: data});

			}
		});
	}

	getAllNews() {
		$.ajax({
			url: "http://localhost:8080/news/news",
			type: "GET",
			dataType: 'json',
			success: (data) => {
				this.setState({news: data});			
			}
		});
	}

	RenderTags(tags){
		return(
				$.each(tags, function() {
				return(
					<TagButton value={this.name}/>
				);
			})
		)	
	}

	// componentDidMount() {
    //     this.RenderTags(this.state.tags);
    // }

	render(){
		return(
			<div class="container-fluid">
				<div className="row">
					<div className="col">
						
						<div class="input-group mb-3">
							<input type="text" class="form-control" placeholder="Recipient's username" aria-label="Recipient's username" aria-describedby="button-addon2"/>
							<div class="input-group-append">
								<button class="btn btn-outline-secondary" type="button" id="button-addon2">Button</button>
							</div>
						</div>

					</div>
				</div>
				<div class="row">
					<div class="col">
						<Header/> 
					</div>
				</div>
				<div class="row">
					<aside class="col-2 px-10" id="left">
						<div class="mx-1 my-1">
							<div class="list-group w-100">
								<a href="#" class="list-group-item">Link</a>
								<a href="#" class="list-group-item">Link</a>
								<a href="#" class="list-group-item">Link</a>
								<a href="#" class="list-group-item">Link</a>
							</div>
						</div>
					</aside>
					<div class="col-10 h-100">
						<TagButton value='sdsdsd'/>
						<TagButton value='sdsdsd'/>
						<NewsCard/>
						<NewsCard/>
						{/* {this.RenderTags(this.state.tags)} */}
					</div>
				</div>
				<div class="row">
					<div class="col">
						<Footer/> 
					</div>
				</div>
  			</div>	
		);
	}
}

// ========================================

ReactDOM.render(
  <Site />,
  document.getElementById('root')
);
