import React from 'react';

import $ from 'jquery'

class AuthorDataService extends React.Component{

    getAllAuthor(callback){
        $.ajax({
            url: "http://localhost:8080/news/authors",
            type: "GET",
            dataType: 'json',
            success: callback,
        });
    }

    getAuthorById(callback, authorId){
        $.ajax({
            url: "http://localhost:8080/news/authors/" + authorId,
            type: "GET",
            dataType: 'json',
            success: callback,
        });
    }

    deleteAuthor(callback, authorId){
        $.ajax({
            url: "http://localhost:8080/news/authors/" + authorId,
            type: "DELETE",
            dataType: 'json',
            success: callback,
        });
    }

    createAuthor(callback, author){
        $.ajax({
            url: "http://localhost:8080/news/authors",
            type: "POST",
            dataType: 'json',
            contentType: 'application/json',
            data: JSON.stringify(author),
            success: callback,
        });
    }

    editAuthor(callback, author){
        $.ajax({
            url: "http://localhost:8080/news/authors",
            type: "PUT",
            dataType: 'json',
            contentType: 'application/json',
            data: JSON.stringify(author),
            success: callback,
        });
    }
}

export default new AuthorDataService()
