import './bootstrap.min.css'
// import './bootstrap.min.js'
import $ from 'jquery'

import Header from './component/Header'
import Footer from './component/Footer'
import TagButton from './component/TagButton'
import NewsCard from './component/NewsCard'
import TagContainer from './component/TagContainer'

import React from 'react';
import ReactDOM from 'react-dom';

class Site extends React.Component {
	constructor(props){
		super(props);
		this.state = {
			tags: [],
			news: [],
		}
	}

	getAllTags() {
		$.ajax({
			url: "http://localhost:8080/news/tags",
			type: "GET",
			dataType: 'json',
			success: (data) => {	
				this.setState({tags: data});

			}
		});
	}

	getAllNews() {
		$.ajax({
			url: "http://localhost:8080/news/news",
			type: "GET",
			dataType: 'json',
			success: (data) => {
				this.setState({news: data});			
			}
		});
	}

	RenderTags(tags){
		return(
				$.each(tags, function() {
				return(
					<TagButton value={this.name}/>
				);
			})
		)	
	}

	// componentDidMount() {
    //     this.RenderTags(this.state.tags);
    // }

	render(){
		return(
			<div className="container-fluid">
				<div className="row">
					<div className="col">
						<Header/> 
					</div>
				</div>
				<div class="row">
					<aside className="col-2 px-10" id="left">
						<div className="mx-1 my-1">
							<div className="list-group w-100">
								<a href="#" className="list-group-item">Link</a>
								<a href="#" className="list-group-item">Link</a>
								<a href="#" className="list-group-item">Link</a>
								<a href="#" className="list-group-item">Link</a>
							</div>
						</div>
					</aside>
					<div className="col-10 h-100">
						<div className="container-fluid">
							<div className="row">
								<div className="col-10 align-items-center">
									<TagButton value='sdsdsd'/>
									<TagButton value='sdsdsd'/>
						
									<div className="input-group mb-3 w-75 my-3">
										<input type="text" className="form-control" placeholder="Author's name" aria-label="Author's name" aria-describedby="button-addon2"/>
										<div className="input-group-append">
											<button className="btn btn-outline-primary" type="button" id="button-addon2">Search</button>
										</div>
									</div>
								
									<NewsCard/>
									<NewsCard/>
									{/* {this.RenderTags(this.state.tags)} */}
								</div>
							</div>
						</div>
					</div>
				</div>
				<div className="row">
					<div className="col">
						<Footer/> 
					</div>
				</div>
  			</div>	
		);
	}
}

// ========================================

ReactDOM.render(
  <Site />,
  document.getElementById('root')
);
