// 	});
// }

class Header extends React.Component {

	render(){
		return(
			<nav class="navbar navbar-expand-md navbar-dark bg-primary justify-content-center">

				<a class="navbar-brand d-flex w-50 mr-auto" href="#">News Portal</a>
			
				<button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#collapsingNavbar" aria-controls="collapsingNavbar" aria-expanded="false" aria-label="Toggle navigation">
					<span class="navbar-toggler-icon"></span>
				</button>

				<div class="navbar-collapse collapse w-100" id="collapsingNavbar">
					<ul class="navbar-nav w-100 justify-content-center">
						<li class="nav-item active">
							<a class="nav-link" href="#">EN</a>
						</li>
						<li class="nav-item active">
							<a class="nav-link" href="#">RU</a>
						</li>
					</ul>
					<ul class="nav navbar-nav ml-auto w-100 justify-content-end">
						<li class="nav-item active">
							<a class="nav-link" href="#">Login</a>
						</li>
						<li class="nav-item active">
							<a class="nav-link" href="#">Register</a>
						</li>
					</ul>
				</div>	
			</nav>
		);
	}
}

class Footer extends React.Component {
	render(){
		return(
			<footer class="footer font-small bg-primary">
				<div class="footer-copyright text-center py-3 text-light">
					© Copyright EPAM 2020: All right reserved
				</div>
			</footer>
		);
	}
}

class Site extends React.Component {
	constructor(props){
		super(props);
		this.state = {
			tags: [],
		}
	}

	getAllTags() {
		$.ajax({
			url: "http://localhost:8080/news/tags",
			type: "GET",
			dataType: 'json',
			success: (data) => {	
				alert(data.length);
				RenderTags(data)
			}
		});
	}

	getAllNews() {
		$.ajax({
			url: "http://localhost:8080/news/news",
			type: "GET",
			dataType: 'json',
			success: (data) => {
				alert(data.length);
				$.each(data, function() {
					return(
						RenderTags(this.tags)
						// <div>
						// 	<TagButton value={this.name}/>
						// </div>
					);
				})
			
			}
		});
	}

	render(){
		return(
			<div class="container-fluid">
				<div class="row">
					<div class="col">
						<Header/> 
					</div>
				</div>
				<div class="row">
					<div class="col-3">
						<span>MENU</span>
					</div>
					<div class="col-9">
						<TagButton value='sdsdsd'/>
						<TagButton value='sdsdsd'/>
						{this.getAllTags()}
					</div>
				</div>
				<div class="row">
					<div class="col">
						<Footer/> 
					</div>
				</div>
  			</div>
						
		);
	}

}


// ========================================

ReactDOM.render(
  <Site />,
  document.getElementById('root')
);
